const char* const HTML_CONTENT = "<!DOCTYPE html><html lang=\"en\"><head><meta name=\"viewport\" content=\"width=device-width,initial-scale=1\"><style>@font-face{font-family:'JetBrains Mono';src:url('path/to/JetBrainsMono-Regular.woff2')format('woff2'),url('path/to/JetBrainsMono-Regular.woff')format('woff');font-weight:normal;font-style:normal;}.slidecontainer{width:100%;}body{-webkit-user-select:none;}:root{--slider-fill-color:#fe73ff;--slider-track-color:#28282800;--slider-border-color:white;--slider-thumb-color:white;--slider-height:10px;--slider-border-width:2px;}.slider{-webkit-appearance:none;width:100%;height:var(--slider-height);background:var(--slider-track-color);border-color:var(--slider-border-color);border-style:solid;border-radius:15px;border-width:var(--slider-border-width);outline:none;opacity:1;transition:opacity .2s;cursor:pointer;width:50%;}.slider:hover{opacity:1;}.slider::-webkit-slider-thumb{-webkit-appearance:none;appearance:none;width:25px;height:25px;border-radius:50%;background:var(--slider-thumb-color);cursor:pointer;}.slider::-moz-range-thumb{width:25px;height:25px;border-radius:50%;background:var(--slider-thumb-color);cursor:pointer;border:none;}.slider::-moz-range-track{background:transparent;border:none;}.slider::-ms-track{background:transparent;border:none;color:transparent;}</style></head><body style='font-family:\"JetBrains Mono\",monospace;color:white;background-color:#282828;'><h1 style='text-align:center;margin:-5px;' id=\"title\"><span style='color:#7fffff'>Ar</span>co</h1><a for=\"title\" style=\"color:#fe73ff;position:fixed;top:23px;right:15px;\">by Nyuu</a><hr style=\"height:2px;background-color:white;border:none;margin:5px;\"><div class=\"slidecontainer\" style=\"position:fixed;top:40px;left:15px\"><p style=\"position:absolute;top:12px;left:2px;\">Arpeggiator Speed</p><input style=\"position:absolute;top:55px;z-index:10;\" type=\"range\" min=\"0\" max=\"1\" value=\"0.5\" step=\"0.01\" class=\"slider\" id=\"myRange\"><img style=\"position:absolute;top:76px;left:2px;filter:invert(100%);width:20px\" src=\"data:image/png;base64,\" alt=\"turtle\"><img style=\"position:absolute;top:78px;left:50%;margin-left:-12px;filter:invert(100%);width:18px\" src=\"data:image/png;base64,\" alt=\"rabbit\"><div style=\"position:absolute;top:63px;left:15px;text-align:center;font-size:40px;letter-spacing:10px;font-family:sans-serif\" id=\"pulse-dots\"><style>.dot-container{display:flex;justify-content:center;align-items:center;padding:20px;}.dot{width:20px;height:5px;border-radius:5px;background-color:#28282800;border:2px solid white;margin:0 12px;opacity:0.3;transition:background-color 0.5s,opacity 0.5s;}.dot.active{opacity:1;}</style><div class=\"dot-container\"><div class=\"dot\"></div><div class=\"dot\"></div><div class=\"dot\"></div><div class=\"dot\"></div><div class=\"dot\"></div></div><script>(function(){const dots=document.querySelectorAll(\"#pulse-dots .dot\");const slider=document.getElementById(\"myRange\");const colors=[\"#7fffff\",\"#fe73ff\"];let current=0,colorToggle=0,timer;let direction=1;function resetDots(){dots.forEach(dot=>{dot.classList.remove(\"active\");dot.style.backgroundColor=\"#28282800\";});}function cycle(){resetDots();const stepTime=(48000/50)*0.25*(0.1+(1.0 - slider.value));const fadeTime=Math.max(50,stepTime*1.5);dots.forEach(dot=>{dot.style.transition=`background-color ${fadeTime}ms,opacity ${fadeTime}ms`;});dots[current].classList.add(\"active\");dots[current].style.backgroundColor=colors[colorToggle];current+=window.arpDirection || 1;if(current>=dots.length || current<0){colorToggle=1 - colorToggle;if((window.arpDirection || 1)===1){current=0;}else{current=dots.length - 1;}}timer=setTimeout(cycle,stepTime);}cycle();})();</script></div><p style=\"color:#00000000;\">Value:<span id=\"demo\">0.5</span></p><!--input type=\"checkbox\" id=\"arp_reverse\"><label for=\"arp_reverse\">Arp Direction</label--></div><div id=\"arp-direction\" style=\"position:fixed;top:160px;display:inline-block;padding:5px 10px;\"><style>#arp-direction .container{display:flex;align-items:center;gap:10px;}#arp-direction .arrow{width:0;height:0;cursor:pointer;}#arp-direction .up{border-left:12px solid transparent;border-right:12px solid transparent;border-bottom:20px solid white;background:transparent;}#arp-direction .down{border-left:12px solid transparent;border-right:12px solid transparent;border-top:20px solid white;background:transparent;}#arp-direction .active.up{border-bottom-color:#fe73ff;}#arp-direction .active.down{border-top-color:#7fffff;}</style><div class=\"container\"><span>Arpeggiator Direction</span><div class=\"arrow up active\"></div><div class=\"arrow down\"></div></div><script>(function(){const root=document.querySelector('#arp-direction');const up=root.querySelector('.up');const down=root.querySelector('.down');window.arpDirection=1;function select(dir){up.classList.remove('active');down.classList.remove('active');if(dir===\"up\"){up.classList.add('active');window.arpDirection=1;window.webkit.messageHandlers.arpReverse.postMessage(Number(0));}else{down.classList.add('active');window.arpDirection=-1;window.webkit.messageHandlers.arpReverse.postMessage(Number(1));}}up.addEventListener('click',()=>select(\"up\"));down.addEventListener('click',()=>select(\"down\"));})();</script></div><script>var slider=document.getElementById(\"myRange\");var output=document.getElementById(\"demo\");function applySliderFill(){const min=parseFloat(slider.min);const max=parseFloat(slider.max);const value=parseFloat(slider.value);const percent=((value - min)/(max - min))*100;slider.style.backgroundImage=`linear-gradient(to right,var(--slider-fill-color)0%,var(--slider-fill-color)${percent}%,var(--slider-track-color)${percent}%,var(--slider-track-color)100%)`;}output.innerHTML=slider.value;applySliderFill();slider.oninput=function(){output.innerHTML=this.value;applySliderFill();window.webkit.messageHandlers.arpSpeedChanged.postMessage(Number(this.value));}</script></body></html>";

